<script>
    document.getElementById('editProfileBtn').addEventListener('click', function(e) {
    e.preventDefault();
    
    // tampilkan modal
    var myModal = new bootstrap.Modal(document.getElementById('editProfileModal'));
    myModal.show();
    
    // load konten form via AJAX
    fetch('<?= base_url('profile/edit') ?>')
        .then(response => response.text())
        .then(html => {
            document.getElementById('editProfileContent').innerHTML = html;
        })
        .catch(err => {
            document.getElementById('editProfileContent').innerHTML = '<p class="text-danger">Gagal memuat form.</p>';
            console.error(err);
        });
});

</script>

